function plugindef()finaleplugin.RequireSelection=true;finaleplugin.Author="Nick Mazuk"finaleplugin.Copyright="CC0 https://creativecommons.org/publicdomain/zero/1.0/"finaleplugin.Version="2.0"finaleplugin.Date="July 10, 2022"finaleplugin.CategoryTags="System"finaleplugin.AuthorURL="https://nickmazuk.com"finaleplugin.MinJWLuaVersion=0.62;finaleplugin.AdditionalMenuOptions="Move System Down"finaleplugin.AdditionalDescriptions="Moves the selected system down one space"finaleplugin.AdditionalPrefixes="move_direction = 1"return"Move System Up","Move System Up","Moves the selected system up one space"end;config={move_amount=24}function system_move(a)local b=finenv.Region()local c=finale.FCStaffSystems()c:LoadAll()local d=b:GetStartMeasure()local e=b:GetEndMeasure()local f=c:FindMeasureNumber(d)local g=c:FindMeasureNumber(e)local h=f:GetItemNo()local i=g:GetItemNo()for j=h,i,1 do local f=c:GetItemAt(j-1)f.SpaceAbove=f.SpaceAbove+a*config.move_amount;f:Save()end end;move_direction=move_direction or-1;system_move(move_direction)